FROM SANDBOX.BASTIENC.HOLIDAY_INN_EXPRESS*/If Object_ID('Warehouse.Selections.HIE010_PreSelection') Is Not Null Drop Table Warehouse.Selections.HIE010_PreSelectionSelect FanIDInto Warehouse.Selections.HIE010_PreSelectionFROM [Relational].[Customer] cuWHERE EXISTS (	SELECT 1				FROM SANDBOX.BASTIENC.HOLIDAY_INN_EXPRESS st				INNER JOIN [Relational].[CINList] cl					ON st.CINID = cl.CINID				WHERE cu.SourceUID = cl.CIN)END